fn main() {
    let hello_world = "Hello, world!";
    println!("{}", hello_world);

    let number = 42;
    println!("{}", number);

    let float_number = 3.14;
    println!("{}", float_number);

    let boolean = true;
    println!("{}", boolean);

    let character = 'a';
    println!("{}", character);
}
